// Accedemos al contenido de `Output Agente` directamente
const fullText = $json['Output Agente'];

// Verificamos si `fullText` contiene los datos esperados
if (!fullText) {
    console.log('No se encontró el texto en la salida de "Output Agente". Verifica la estructura del JSON de salida.');
    return [{ error: 'No se encontró el texto en la salida de "Output Agente".' }];
}

// Expresión regular para detectar URLs dentro y fuera del formato Markdown
const urlRegex = /\[.*?\]\((https?:\/\/[^\s)]+)\)|\bhttps?:\/\/[^\s]+/g;

// Extraemos todas las URLs del texto
const urls = [];
let cleanText = fullText;

// Reemplazamos las URLs encontradas por un espacio en el texto limpio
cleanText = cleanText.replace(urlRegex, (match, markdownUrl) => {
    const url = markdownUrl || match; // Extrae la URL correcta
    urls.push(url);
    return ''; // Reemplaza la URL en el texto con un espacio
}).trim();

// Construimos el resultado con texto limpio y URLs
return [
    {
        json: {
            text: cleanText,
            urls: urls.length > 0 ? urls : null // Retorna array de URLs si hay, sino null
        }
    }
];

// Obtener el texto de la variable de manera dinámica
const inputText = items[0].json[Object.keys(items[0].json)[0]]; // Accede al primer campo dinámicamente

// Expresión regular para detectar URLs dentro y fuera del formato Markdown
const urlRegex = /\[.*?\]\((https?:\/\/[^\s)]+)\)|\bhttps?:\/\/[^\s]+/g;

// Buscar todas las URLs en el texto
const urls = [];
let match;
while ((match = urlRegex.exec(inputText)) !== null) {
    // Si el primer grupo existe (URL en formato Markdown), usarlo; de lo contrario, usar el match completo
    urls.push(match[1] || match[0]);
}

// Retornar los resultados
return [
    {
        json: {
            urls: urls.length > 0 ? urls : null // Retorna array de URLs si hay, sino null
        }
    }
];
