// 1. Obtener el string de CustomFields y parsearlo si es necesario
let customFields = $input.first().json.CustomFields;

// Si viene como string, lo parseamos a objeto
if (typeof customFields === "string") {
  try {
    customFields = JSON.parse(customFields);
  } catch (e) {
    return [{ json: { error: "No se pudo parsear CustomFields" } }];
  }
}

// 2. Definir los estados posibles del funnel
const estadosFunnel = ["menos50%", "mas50%", "Agenda", "No Asiste", "Seguimiento"];

// 3. Buscar coincidencias
let etapaDetectada = null;

for (const field of customFields) {
  for (const estado of estadosFunnel) {
    if (field.value && typeof field.value === "string" && field.value.includes(estado)) {
      etapaDetectada = estado;
      break;
    }
  }
  if (etapaDetectada) break;
}

// 4. Devolver resultado
return [
  {
    json: etapaDetectada
      ? { etapa: etapaDetectada }
      : { etapa: "No detectada" }
  }
];

