// 1. Obtener el string de CustomFields
const rawFields = $input.first().json.CustomFields;

// 2. Parsear a JSON
const customFields = JSON.parse(rawFields);

// 3. Mapeo de IDs a nombres amigables
const fieldMap = {
  "zUNqnC0tbcfdAVjCTJ7k": "Enlace_reunión",
  "oSokK0XbB2I4nlT0OHku": "Fecha_reunión",
  "H6AKfDHo6YgOP0Jv7hWW": "Etapa"
};

// 4. Recorrer y construir resultado
const result = {};

for (const field of customFields) {
  if (fieldMap[field.id]) {
    result[fieldMap[field.id]] = field.value;
  }
}

// 5. Devolver el resultado o error
return [
  {
    json: Object.keys(result).length > 0 ? result : { error: "Campos no encontrados" }
  }
];

// Este código se ejecuta sobre cada item del flujo
return items.map(item => {
	const etiquetas = $input.first().json['Mónica'] || [];

	const tieneMonica = etiquetas.includes("monica on");

	item.json.resultado = tieneMonica ? "SI" : "NO";

	return item;
});

return items.map(item => {
    const texto = $input.first().json.Texto;
    const imagen = $input.first().json.Imagen;
    const audio = $input.first().json.Audio;

    let resultado = "";

    if (texto && texto !== "[null]") {
        resultado = texto;
    } else if (imagen && imagen !== "[null]") {
        resultado = imagen;
    } else if (audio && audio !== "[null]") {
        resultado = audio;
    }

    return {
        json: {
            TEXT: resultado
        }
    };
});

